import React, {useEffect, useState} from "react";
import Link from "next/link";

import {register} from "./Services/Users";

import "./styles/imports.scss";


const LoginPage = () => {
    const [register, setRegister] = useState({   
                                            username: '',
                                            email: '',
                                            password: '',
                                            password2: '',
                                            submitted: false,
                                            loading: false,
                                            error: ''
                                        }); 
       
    
    const handleChange = (e) => {
        const { name, value } = e.target;
        setRegister({...login, [name]: value, "error": "" });
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        setRegister({...login, submitted: true });
        const { username, password } = login;
        // stop here if form is invalid
        if (!(username && password)) {
            return;
        }
        setRegister({...login, loading: true });
        authenticate(username, password)
        // .then(data => {data && console.log(data)})
    }

        return(
             <div className="" >
                    <img src="" alt="logo" className="" />
                    <div className="">
                        <form name="form" onSubmit={(e) => handleSubmit(e)} className=""> 
                            <div className="">
                                <input type="text" className="" name="username" value={login.username} onChange={handleChange} placeholder="USERNAME"/>
                                {login.submitted && !login.username &&
                                    <div className="help-block">Username is required</div>
                                }
                                <input type="email" className="" name="password" value={login.email} onChange={handleChange} placeholder="EMAIL"/>
                                {login.submitted && !login.email &&
                                    <div className="help-block">Email is required</div>
                                }
                                <input type="password" className="" name="password" value={login.password} onChange={handleChange} placeholder="PASSWORD"/>
                                {login.submitted && !login.password &&
                                    <div className="help-block">Password is required</div>
                                }
                                <input type="password" className="" name="password2" value={login.password2} onChange={handleChange} placeholder="RETYPE PASSWORD"/>
                                {login.submitted && !login.password2 &&
                                    <div className="help-block">Please retype password</div>
                                }
                            </div>
                            <span className="form__error">{login.error}</span>
                            <div className="" >
                                <Link href="/dashboard">
                                    <a>Go</a>
                                </Link>
                                <Link href="/register">
                                    <h1>register</h1>
                                </Link>
                            </div>
                        </form>
                    </div>
                </div>
            );
}

export default LoginPage;
